    @Override
    public void stateChanged(ChangeEvent e) {
        JSlider source = (JSlider)e.getSource();
        if(!source.getValueIsAdjusting() )
        {
            jProgressBar1.setValue(source.getValue());
            jProgressBar2.setValue(source.getValue()/2);
            jProgressBar3.setValue(source.getValue()/4);
            jProgressBar4.setValue(source.getValue()/8);
            System.out.println(source.getValue());
        }
        else
            System.out.println("notAdjusting" + source.getValue());
    }
